<?phpif ( function_exists('register_sidebar') )    register_sidebar();?><?php	function bdw_get_images($iPostID) {     // Get the post ID         // Get images for this post    $arrImages =& get_children('post_type=attachment&post_mime_type=image&post_parent=' . $iPostID );     // If images exist for this page    if($arrImages) {         // Get array keys representing attached image numbers        $arrKeys = array_keys($arrImages);                  // Get the first image attachment        $iNum = $arrKeys[0];         // Get the thumbnail url for the attachment        $sThumbUrl = wp_get_attachment_thumb_url($iNum);		return $sThumbUrl;         /*//UNCOMMENT THIS IF YOU WANT THE FULL SIZE IMAGE INSTEAD OF THE THUMBNAIL        $sImageUrl = wp_get_attachment_url($iNum);		return $sImageUrl;*/         /*Build the <img> string        // $sImgString = '<a h	ref="' . get_permalink() . '">' .                            // '<img src="' . $sThumbUrl . '" width="150" height="150" alt="Thumbnail Image" title="Thumbnail Image" />' .                        // '</a>';         Print the image        // echo $sImgString; */    }	else	{		return 0;	}}function custom_excerpt_length( $length ) {	return 15;}add_filter( 'excerpt_length', 'custom_excerpt_length', 999 );function the_post_thumbnail_caption() {  global $post;  $thumbnail_id    = get_post_thumbnail_id($post->ID);  $thumbnail_image = get_posts(array('p' => $thumbnail_id, 'post_type' => 'attachment'));   if ($thumbnail_image && isset($thumbnail_image[0])) {    echo '<p>'.$thumbnail_image[0]->post_title.'&nbsp;'.$thumbnail_image[0]->post_content.'</p>';  }}function remove_images( $content ) {   $postOutput = preg_replace('/<img[^>]+./','', $content);   return $postOutput;}add_filter( 'the_content', 'remove_images', 100 );?>